    @Test
    @DisplayName("Object Mapper 동작 실패 테스트")
    public void objectMapperToOpenApiResponseFailTest() throws NoSuchMethodException {
        // given
        final String testJson = "{ abc";

        // when
        Method method = BlogOpenApiWrapperKakaoImpl.class.getDeclaredMethod("makeResponseInstance", String.class);
        method.setAccessible(true);
        Exception exception = assertThrows(InvocationTargetException.class,
                () -> method.invoke(openApi, testJson));

        // then
        Assertions.assertEquals(InvocationTargetException.class, exception.getClass());
    }

